pw.component.register("notifier",function(view,config){this.listen("notification:published");this.listen("response:received");view.node.addEventListener("click",function(evt){view.node.classList.add("hide")});this.message=function(channel,payload){if(channel==="response:received"){var notification=payload.response.headers["Pakyow-Notify"];if(notification){view.node.innerText=notification;view.node.classList.remove("hide")}}else if(channel==="notification:published"){view.node.innerText=payload.notification;view.node.classList.remove("hide")}}});
